mapper.insert:
INSERT INTO KHJ_BOARD
	( KHJ_ID
	  , KHJ_NAME
	  , KHJ_DESCRIPTION
	  , KHJ_USE_YN
	  , KHJ_REG_USER )
VALUES ( #{id}
	  , #{name}
	  , #{description}
	  , #{useYn}
	  , #{regUser} )

mapper.update:
